mapper.select:
 SELECT share_id,s.file_id,s.user_id,valid_type,expire_time, share_time,code,show_count

	 ,f.file_name,f.folder_type,f.file_category,f.file_type,f.file_cover

 FROM file_share s

	 left join file_info f on s.file_id = f.file_id and f.user_id = #{query.userId}


	 and share_id = #{query.shareId} 
	 and s.file_id = #{query.fileId} 
	 and s.user_id = #{query.userId} 
	 and valid_type = #{query.validType} 

		 and expire_time=str_to_date(#{query.expireTime}, '%Y-%m-%d') 


		 and share_time=str_to_date(#{query.shareTime}, '%Y-%m-%d') 

	 and code = #{query.code} 
	 and show_count = #{query.showCount} 
	 and share_id like concat('%', #{query.shareIdFuzzy}, '%') 
	 and s.file_id like concat('%', #{query.fileIdFuzzy}, '%') 
	 and s.user_id like concat('%', #{query.userIdFuzzy}, '%') 

		 and expire_time>=str_to_date(#{query.expireTimeStart}, '%Y-%m-%d') 


		 and expire_time< date_sub(str_to_date(#{query.expireTimeEnd},'%Y-%m-%d'),interval -1 day) 


		 and share_time>=str_to_date(#{query.shareTimeStart}, '%Y-%m-%d') 


		 and share_time< date_sub(str_to_date(#{query.shareTimeEnd},'%Y-%m-%d'),interval -1 day) 

	 and code like concat('%', #{query.codeFuzzy}, '%') 


	 order by ${query.orderBy}


	 limit #{query.simplePage.start},#{query.simplePage.end}

SELECT count(1) FROM file_share

	 and share_id = #{query.shareId} 
	 and file_id = #{query.fileId} 
	 and user_id = #{query.userId} 
	 and valid_type = #{query.validType} 

		 and expire_time=str_to_date(#{query.expireTime}, '%Y-%m-%d') 


		 and share_time=str_to_date(#{query.shareTime}, '%Y-%m-%d') 

	 and code = #{query.code} 
	 and show_count = #{query.showCount} 
	 and share_id like concat('%', #{query.shareIdFuzzy}, '%') 
	 and file_id like concat('%', #{query.fileIdFuzzy}, '%') 
	 and user_id like concat('%', #{query.userIdFuzzy}, '%') 

		 and expire_time>=str_to_date(#{query.expireTimeStart}, '%Y-%m-%d') 


		 and expire_time< date_sub(str_to_date(#{query.expireTimeEnd},'%Y-%m-%d'),interval -1 day) 


		 and share_time>=str_to_date(#{query.shareTimeStart}, '%Y-%m-%d') 


		 and share_time< date_sub(str_to_date(#{query.shareTimeEnd},'%Y-%m-%d'),interval -1 day) 

	 and code like concat('%', #{query.codeFuzzy}, '%')

mapper.update:
update file_share
set show_count = show_count + 1
where share_id = #{shareId};

mapper.delete:
delete
from file_share
where user_id = #{userId} and share_id in (#{item});
